    private void btnIniciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniciarActionPerformed
        String porta = txtPorta.getText();
        int port = Integer.parseInt(porta);
        try {
            (new TCPServerAtivosMain(port, this)).start();
            addMessageLog("Servidor iniciado na porta: " + port);
            btnIniciar.setEnabled(false);
        } catch (IOException ex) {
            JOptionPane.showMessageDialog(this, ex.getMessage(), "ERRO", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnIniciarActionPerformed

    public void addMessageLog(String mensagem) {
        txtAreaLog.append(mensagem + "\n");
    }

